import React, {useState} from 'react';
import axios from 'axios';

import './chatroom.css';

export default function Chatroom() { 
  const roomName = localStorage.getItem('roomName');
  const username = localStorage.getItem('username');
  const isPrivateChatroom = localStorage.getItem('private room');
  const [roomCode, setRoomCode] = useState(undefined);

  if (isPrivateChatroom) {
    axios.get('https://crow249.herokuapp.com/rooms/list')
      .then((response) => {
        if (response.data){
          setRoomCode(response.data[response.data.length - 1]._id.$oid);
        }
      })
      .catch(error => {
        console.log(error);
        setError(error);
      })
  } 
  else { setRoomCode(false); }
  return (
    <>
      <div className="wrapper">
	<div className="topBar">
	  <div className="roomName"> {roomName} </div>
	</div>
      </div>
      <div className="sidebar">
        <div className="sidebarContent">
	  <h3> User 1 </h3>
	  <h3> User 2 </h3>
	</div>
      </div>
      <div className="chatbar">
        <input
	  className="chatInput"
          placeholder="Type a message"
        />
      </div>
      <div className="chatWindow"></div>
    </>
  )
}
