import React from 'react';
import {useHistory} from 'react-router-dom';

import './usernameChoice.css';
import '../Home/home.css';

export default function UsernameChoice() {
  const history = useHistory();

  function navigateToPage(path) {
    history.push(path);
  }

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [username, setUsername] = useState('');

  return (
    <div className="content choice">
      {isModalOpen &&
        <div className="createRoomModal">
          <input
            className="usernameInput"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
      }
      <div>
	<button
          onClick={() => history.push('/')}
          className="button choice"
        >
          {"<--"}Go Back Home
        </button>
      </div>
      <h3> Let's pick a username </h3>
      <button className="page-button home">
        Choose for me
      </button>
      <button
        onClick={() => navigateToPage('/usernameChoice/usernames')}
	className="page-button home"
      >
	Pick a Default Username
      </button>
      <button
	onClick={() => setIsModalOpen(true)} 
	className="page-button home"
      >
        Create my Own
      </button>		
    </div>
  );
};
