import React, {useState, useEffect} from 'react';
import axios from 'axios';
import {useHistory, useParams} from 'react-router-dom';

import './chatroom.css';

export default function Chatroom() { 
  const history = useHistory();
  const params = useParams();
  const roomID = params.roomID;

  const [data, setData] = useState(undefined);
  const [isLoading, setIsLoading] = useState(true);

  const getRoomData = () => {
    return axios.get('https://crow249.herokuapp.com/rooms/list');
  }

  useEffect(async() => {
    const data = await getRoomData()
			.catch((error) => console.log(error));
    for (var i = 0; i < data.data.length; ++i) {
      if (data.data[i]._id.$oid == roomID) {
        setData(data.data[i]);
        console.log(data.data[i]);
        setIsLoading(false);
        break;
      }
    }
  }, []);

  return (
    <>
    {!isLoading && (
    <div className="content chatroom">
      <div className="sideBarContainer">
        <div className="topBar">
          <button
	    className="topBarTitle chatroom"
	    onClick={() => history.push('/')}
          >            
	    Crow
          </button>
        </div>
        <div className="sidebar">
          <div className="sidebarContent">
            {data.list_users ? data.list_users.map((user, index) => (
              <div
                key={`${user}-${index}`}
              >
                <h3> {user} </h3>
              </div>
            )) : (
              <h3></h3>
            )}
          </div>
        </div>
      </div>
      <div className="chatContainer">
        <div className="topBar chat">
          <div className="roomName"> {data.room_name} </div>
          <h4 style={{color: "white", marginLeft: "225px"}}> {data._id.$oid} </h4>
        </div>
        <div className="chatWindow"></div>
        <div className="chatbar">
          <input
	    className="chatInput"
            placeholder="Type a message"
          />
        </div>
      </div>
    </div>
    )}
    </>
  )
}
